function ExpenseDate(props) {
    const month = props.date.toLocaleString('en-US', { month : 'short'})
    const day = props.day.toLocaleString('en-US', { year : '2-digit'})
    const year = props.year.getFullYear();

    return (
        <div>
            <div>{month}</div>
            <div>{day}</div>
            <div>{year}</div>
        </div>
    )
}

export default ExpenseDate
